    public boolean validateMail(String email) {
        int atpos = email.indexOf("@");
        int dotpos = email.lastIndexOf(".");
        if (atpos > 2 && (dotpos - atpos) > 2) {
            return true;
        } else {
            return false;
        }
    }
    private void btnAddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAddActionPerformed
        if (Integer.parseInt(txtId.getText()) < 2) {
            JOptionPane.showMessageDialog(null, "Enter your ID");
        } else if (txtName.getText().trim().length() < 3) {
            JOptionPane.showMessageDialog(null, "Enter your Name");
        } else if (Integer.parseInt(txtAge.getText()) < 18 || Integer.parseInt(txtAge.getText()) > 40) {
            JOptionPane.showMessageDialog(null, "Enter your Age between 18-40");
        } else if (!rMale.isSelected() && !rFemale.isSelected()) {
            JOptionPane.showMessageDialog(null, "Select your Gender");
        } else if (!validateMail(txtEmail.getText())) {
            JOptionPane.showMessageDialog(null, "Enter your valid Email");
        } else if (!chkOne.isSelected() && !chkTwo.isSelected()) {
            JOptionPane.showMessageDialog(null, "Select your Course");
        } else if (cmbround.getItemAt(cmbround.getSelectedIndex()).equalsIgnoreCase("select a round")) {
            JOptionPane.showMessageDialog(null, "Select your Round");
        } else if (txtMessage.getText().length() < 7) {
            JOptionPane.showMessageDialog(null, "Enter your message");
        } else {
            int id = Integer.parseInt(txtId.getText());
            String name = txtName.getText();
            int age = Integer.parseInt(txtAge.getText());
            String gender = "";
            if (rMale.isSelected()) {
                gender = rMale.getText();
            }
            if (rFemale.isSelected()) {
                gender = rFemale.getText();
            }
            String email = txtEmail.getText();
            String course = "";
            if (chkOne.isSelected()) {
                course = chkOne.getText() +", ";
            }
            if (chkTwo.isSelected()) {
                course += chkTwo.getText();
            }
            String round = cmbround.getItemAt(cmbround.getSelectedIndex());
            String message = txtMessage.getText();

            DefaultTableModel model = (DefaultTableModel) tblDisplay.getModel();
            Object[] cols = new Object[8];
            cols[0] = id;
            cols[1] = name;
            cols[2] = age;
            cols[3] = gender;
            cols[4] = email;
            cols[5] = course;
            cols[6] = round;
            cols[7] = message;
            model.addRow(cols);

            Student student = new Student(id, name, age, gender, email, course, round, message);
            List<Student> list = new ArrayList<>();
            list.add(student);

            Methods.writeToFile(JOptionPane.showInputDialog("Enter file name to save"), list);
        }
    }//GEN-LAST:event_btnAddActionPerformed

    private void btnReadActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnReadActionPerformed
        DefaultTableModel model = (DefaultTableModel) tblDisplay.getModel();
        Methods.readToTable(JOptionPane.showInputDialog("Enter file name to read"), model);
    }//GEN-LAST:event_btnReadActionPerformed

    private void btnClearActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnClearActionPerformed
        txtId.setText("0");
        txtName.setText("");
        txtAge.setText("0");
        btgGender.clearSelection();
        txtEmail.setText("");
        chkOne.setSelected(false);
        chkTwo.setSelected(false);
        cmbround.setSelectedIndex(0);
        txtMessage.setText("");
    }//GEN-LAST:event_btnClearActionPerformed

    private void btnClearTableActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnClearTableActionPerformed
        DefaultTableModel model = (DefaultTableModel) tblDisplay.getModel();
        model.setRowCount(0);
    }//GEN-LAST:event_btnClearTableActionPerformed

    private void btnExitActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnExitActionPerformed
        int dialogBtn = 0;
        int dialogRslt = JOptionPane.showConfirmDialog(null, "Are you sure ?", "Warning !", dialogBtn);
        if (dialogRslt == JOptionPane.YES_OPTION) {
            System.out.println("Program Closed !");
            System.exit(0);
        } else {
            JOptionPane.showMessageDialog(null, "Okay ! Complete your task.");
        }
    }//GEN-LAST:event_btnExitActionPerformed

